import React from "react";
import "../styles/tour-details.css";
import { Container, Row, Col } from "reactstrap";
import { useParams } from "react-router-dom";
import tourData from "../assets/data/tours";

const TourDetails = () => {
  const { id } = useParams();
  const tour = tourData.find((tour) => tour.id === parseInt(id)); // Chuyển id thành số

  // Kiểm tra nếu không tìm thấy tour
  if (!tour) {
    return (
      <section>
        <Container>
          <Row>
            <Col lg="12" className="text-center">
              <h2 className="text-danger">Tour không tồn tại!</h2>
              <p>Chúng tôi không tìm thấy tour bạn đang tìm kiếm.</p>
            </Col>
          </Row>
        </Container>
      </section>
    );
  }

  // Destructuring tour
  const { photo, title, desc, price, reviews, city, distance, maxGroupSize } =
    tour;

  return (
    <>
      <section>
        <Container>
          <Row>
            <Col lg="8">
              <div className="tour__content">
                <img src={photo} alt={title} className="tour__image" />
                <div className="tour__info">
                  <h2>{title}</h2>
                  <p className="tour__desc">{desc}</p>
                  <ul className="tour__details">
                    <li>
                      <strong>Thành phố:</strong> {city}
                    </li>
                    <li>
                      <strong>Khoảng cách:</strong> {distance} km
                    </li>
                    <li>
                      <strong>Nhóm tối đa:</strong> {maxGroupSize} người
                    </li>
                    <li>
                      <strong>Giá:</strong> ${price} / người
                    </li>
                  </ul>
                </div>
              </div>
            </Col>
            <Col lg="4">
              <div className="tour__reviews">
                <h4>Đánh giá ({reviews.length})</h4>
                <ul className="review__list">
                  {reviews.length > 0 ? (
                    reviews.map((review, index) => (
                      <li key={index} className="review__item">
                        <p>
                          <strong>{review.username}</strong>: {review.comment}
                        </p>
                        <span>⭐ {review.rating}/5</span>
                      </li>
                    ))
                  ) : (
                    <p>Chưa có đánh giá nào.</p>
                  )}
                </ul>
              </div>
            </Col>
          </Row>
        </Container>
      </section>
    </>
  );
};

export default TourDetails;
