import React from "react";
import '../styles/tour-details.css';
import { Container, Row, Col } from 'reactstrap';
import { useParams } from 'react-router-dom';
import tourData from '../assets/data/tours';

const TourDetails = () => {
  const { id } = useParams();
  const tour = tourData.find(tour => tour.id === id);

  // Kiểm tra nếu không tìm thấy tour
  if (!tour) {
    return (
      <section className="tour__not-found">
        <Container>
          <Row>
            <Col lg="12" className="text-center">
              <h2 className="tour__not-found-title">Tour không tồn tại!</h2>
              <p className="tour__not-found-text">Vui lòng kiểm tra lại danh sách tour.</p>
            </Col>
          </Row>
        </Container>
      </section>
    );
  }

  // Giải nén thông tin tour
  const { photo, title, desc, price, reviews, city, distance, maxGroupSize } = tour;

  return (
    <>
      <section>
        <Container>
          <Row>
            <Col lg="8">
              <div className="tour__content">
                <img src={photo} alt={title} className="tour__image" />
                <div className="tour__info">
                  <h2 className="tour__title">{title}</h2>
                  <div className="tour__meta">
                    <span className="tour__location"><i className="ri-map-pin-line"></i> {city}</span>
                    <span className="tour__distance"><i className="ri-road-map-line"></i> {distance} km</span>
                    <span className="tour__group-size"><i className="ri-group-line"></i> Nhóm tối đa: {maxGroupSize} người</span>
                  </div>
                  <h3 className="tour__price">${price} / người</h3>
                  <p className="tour__desc">{desc}</p>
                </div>
              </div>
            </Col>
          </Row>
        </Container>
      </section>
    </>
  );
};

export default TourDetails;
