import React, {useState,useEffect} from 'react'
import CommonSection from '../shared/CommonSection'
import SearchBar from '../shared/SearchBar'
import TourCard from '../shared/TourCard'

import tourData from '../assets/data/tours'

import { Container, Row, Col} from "reactstrap"
import Newsletter from '../shared/Newsleter'
import useFetch from '../hooks/useFetch'
import { BASE_URL } from '../utils/config'
const Tour = () => {

  const [pageCount, setPageCount ]= useState(0)
  const [page,setPage]= useState(0)

  const {data:tour,loading,error}= useFetch(`${BASE_URL}/tour`)
  const {data:tourCount}= useFetch(`${BASE_URL}/tour/search/getTourCount`)
  useEffect(()=>{
    const pages =Math.ceil(tourCount/ 4);
    setPageCount(pages);

  },[page, tourCount])
  return (<>
  <CommonSection title ={'Toàn Bộ Tours'}/>
    <section>
       <Container>
        <Row>
          <SearchBar/>

        </Row>
       </Container>
    </section>
    <section className='pt-0'>

      <Container>
        <Row>
          {
            !loading &&
            tour?.map(tour => <Col lg='3'className='mb-4' key ={tour.id}><TourCard tour={tour}/></Col>)
          }

          <Col lg='12'>
          <div className="pagination  d-flex align-items-center justify-content-center mt-4  gap-3 " >
            {[...Array(pageCount).keys()].map(number=>(
              <span key={number} onClick={()=>setPage(number)} className={page=== number ? "active__page": ""}>
                {number + 1}
              </span>
            ))}
          </div>
          </Col>
        </Row>
      </Container>
    </section>
    <Newsletter/>


  </>
  )
}

export default Tour;
