import React from 'react';
import { Container, Row, Col } from 'reactstrap';
import TourCard from '../shared/TourCard';
import useFetch from '../hooks/useFetch';
import { BASE_URL } from '../utils/config';
import { useLocation } from 'react-router-dom';

const SearchResultList = () => {
  const location = useLocation();
  const queryParams = location.search; // Lấy chuỗi ?city=...&distance=...
  const { data: searchedTours, loading, error } = useFetch(`${BASE_URL}/tour/search/getTourBySearch${queryParams}`);

  return (
    <section>
      <Container>
        <Row>
          {loading && <h4>Đang tìm kiếm tour...</h4>}
          {error && <h4>Lỗi: {error}</h4>}
          {!loading && !error && searchedTours?.length === 0 && (
            <h4>Không tìm thấy tour phù hợp.</h4>
          )}
          {!loading && !error && searchedTours?.map((tour) => (
            <Col lg="3" className="mb-4" key={tour._id}>
              <TourCard tour={tour} />
            </Col>
          ))}
        </Row>
      </Container>
    </section>
  );
};

export default SearchResultList;
