import React from 'react';
import { Container, Row, Col } from 'reactstrap';
import '../styles/home.css'

import heroImg from '../assets/images/hero-img01.jpg';
import heroImg02 from '../assets/images/hero-img02.jpg';
import heroVideo from '../assets/images/hero-video.mp4';
import worldImg from '../assets/images/world.png'
const Home = () => {
  return (
    <>
      {/* -------------- hero section start -------------- */}
      <section>
        <Container>
          <Row>
            <Col lg='6'>
              <div className="hero__content">
                <div className="hero__subtitle d-flex align-items-center">
                  <Subtitle subtitle={'Hãy tìm hiểu thật kỹ trước khi lên đường, vì một chuyến đi trọn vẹn bắt đầu từ sự chuẩn bị chu đáo.'} />
                  <img src={worldImg} alt="" />
                </div>
                <h1>
                Du lịch là cách để tâm hồn được tự do và trái tim được rộng mở. <span 
                className="highlight">memories</span></h1>
                <p>Hãy ngồi xuống, hít thở không khí trong lành và cảm nhận sự kết nối giữa thiên nhiên và con người.
                  Du lịch không chỉ là khám phá những vùng đất mới, mà còn là cơ hội để ta tìm lại chính mình, 
                  để tâm hồn được thảnh thơi và trái tim được rộng mở. Những khoảnh khắc chia sẻ niềm vui bên bạn bè, người thân,
                  hay thậm chí là những người bạn mới quen, sẽ trở thành hành trang quý giá trong cuộc đời. Dù có khó khăn hay bộn bề, 
                  chỉ cần bước chân ra thế giới, mọi muộn phiền sẽ nhẹ nhàng tan biến, nhường chỗ cho sự bình yên và hạnh phúc giản đơn.
                  Du lịch là liều thuốc cho tâm hồn, giúp ta nhận ra rằng cuộc sống này đẹp đẽ biết bao khi ta biết dừng lại, lắng nghe và tận hưởng.   </p>
              </div>
            </Col>
          </Row>
        </Container>
      </section>
      {/* -------------- hero section end -------------- */}
    </>
  );
};

export default Home;
